];

// ÌîÑÎ°ùÏãúÎ•º ÏÇ¨Ïö©Ìïú Ïõπ Ïä§ÌÅ¨ÎûòÌïë
const fetchWithProxy = async (url, proxyIndex = 0) => {
  if (proxyIndex >= PROXY_SERVICES.length) {
    throw new Error('All proxy attempts failed');
  }

  try {
    const proxyUrl = PROXY_SERVICES[proxyIndex] + url;
    console.log(`üîó Trying proxy ${proxyIndex + 1}/${PROXY_SERVICES.length}: ${proxyUrl.substring(0, 50)}...`);
    
    const response = await fetch(proxyUrl, {
      method: 'GET',
      headers: {
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
        'Accept-Language': 'en-US,en;q=0.9',
        'Accept-Encoding': 'gzip, deflate, br',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1'
      },
      timeout: 10000 // 10Ï¥à ÌÉÄÏûÑÏïÑÏõÉ
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const html = await response.text();
    
    if (html.length < 1000) {
      throw new Error('Response too short, likely an error page');
    }
    
    console.log(`‚úÖ Proxy ${proxyIndex + 1} successful! HTML length: ${html.length} characters`);
    return html;
  } catch (error) {
    console.log(`‚ùå Proxy ${proxyIndex + 1} failed: ${error.message}`);
    return fetchWithProxy(url, proxyIndex + 1);
  }
};

// HTMLÏóêÏÑú Í∏àÎ¶¨ Îç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú (Trading Economics Calendar ÌÖåÏù¥Î∏îÏóêÏÑú Ï∂îÏ∂ú)
const extractInterestRateFromHTML = (html, country) => {
  try {
    // Trading Economics Calendar ÌÖåÏù¥Î∏îÏóêÏÑú Í∞ÄÏû• ÏµúÏã†Ïùò Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞ TEForcast Í∞í Ï∂îÏ∂ú
    
    // ÌÖåÏù¥Î∏î Ìå®ÌÑ¥ Ï∞æÍ∏∞ (Calendar, GMTÍ∞Ä Ìè¨Ìï®Îêú ÌÖåÏù¥Î∏î)
    const tablePattern = /<table[^>]*>.*?Calendar.*?GMT.*?<\/table>/is;
    const tableMatch = html.match(tablePattern);
    
    if (!tableMatch) {
      console.log(`No calendar table found for ${country}`);
      return null;
    }
    
    const tableHTML = tableMatch[0];
    
    // ÌÖåÏù¥Î∏î ÌñâÎì§ Ï∂îÏ∂ú
    const rowPattern = /<tr[^>]*>.*?<\/tr>/gis;
    const rows = tableHTML.match(rowPattern);
    
    if (!rows || rows.length < 2) {
      console.log(`No rows found in calendar table for ${country}`);
      return null;
    }
    
    // ÌòÑÏû¨ ÎÇ†Ïßú Í∞ÄÏ†∏Ïò§Í∏∞
    const currentDate = new Date();
    const currentDateString = currentDate.toISOString().split('T')[0]; // YYYY-MM-DD ÌòïÏãù
    
    console.log(`Current date: ${currentDateString}`);
    
    // Ï≤´ Î≤àÏß∏ ÌñâÏùÄ Ìó§ÎçîÏù¥ÎØÄÎ°ú Ï†úÏô∏ÌïòÍ≥†, Îëê Î≤àÏß∏ ÌñâÎ∂ÄÌÑ∞ Í≤ÄÏÉâ
    // Í∞ÄÏû• ÏµúÏã†Ïùò Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞(Actual Í∞íÏù¥ ÏûàÎäî Ìñâ)Ïùò TEForcast Í∞í Ï∞æÍ∏∞
    let latestRate = null;
    let latestDate = null;
    
    for (let i = 1; i < rows.length; i++) {
      const row = rows[i];
      
      // TD ÌÉúÍ∑∏Îì§ Ï∂îÏ∂ú
      const tdPattern = /<td[^>]*>(.*?)<\/td>/gis;
      const tds = row.match(tdPattern);
      
      if (tds && tds.length >= 7) { // Calendar, GMT, Reference, Actual, Previous, Consensus, TEForecast
        // Ï≤´ Î≤àÏß∏ TD: ÎÇ†Ïßú
        const dateTD = tds[0];
        // ÎÑ§ Î≤àÏß∏ TD: Actual Í∞í
        const actualTD = tds[3];
        // ÏùºÍ≥± Î≤àÏß∏ TD: TEForcast Í∞í
        const teForcastTD = tds[6];
        
        // ÎÇ†Ïßú Ï∂îÏ∂ú (YYYY-MM-DD ÌòïÏãù)
        const datePattern = /(\d{4}-\d{2}-\d{2})/;
        const dateMatch = dateTD.match(datePattern);
        
        if (dateMatch) {
          const date = dateMatch[1];
          
          // ÎÇ†ÏßúÍ∞Ä ÌòÑÏû¨ ÎÇ†ÏßúÎ≥¥Îã§ ÎØ∏ÎûòÏù∏ÏßÄ ÌôïÏù∏
          if (date > currentDateString) {
            console.log(`Skipping future date: ${date}`);
            continue; // ÎØ∏Îûò ÎÇ†ÏßúÎäî Í±¥ÎÑàÎõ∞Í∏∞
          }
          
          // Actual Í∞íÏù¥ ÏûàÎäîÏßÄ ÌôïÏù∏ (Îπà Í∞íÏù¥ ÏïÑÎãåÏßÄ)
          const actualPattern = /(\d+\.\d+)/;
          const actualMatch = actualTD.match(actualPattern);
          
          if (actualMatch) {
            // Actual Í∞íÏù¥ ÏûàÏúºÎ©¥ Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞Î°ú Í∞ÑÏ£º
            const teForcastPattern = /(\d+\.\d+)/;
            const teForcastMatch = teForcastTD.match(teForcastPattern);
            
            if (teForcastMatch) {
              const rate = parseFloat(teForcastMatch[1]);
              
              // Îçî ÏµúÏã† ÎÇ†ÏßúÏù∏ÏßÄ ÌôïÏù∏
              if (!latestDate || date > latestDate) {
                latestDate = date;
                latestRate = rate;
                console.log(`Found actual data: ${rate}% for date: ${date}`);
              }
            }
          } else {
            console.log(`No actual data for date: ${date}, skipping`);
          }
        }
      }
    }
    
    if (latestRate) {
      console.log(`Found latest actual rate for ${country}: ${latestRate}% (Date: ${latestDate})`);
      return latestRate;
    }
    
    // Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞Î•º Ï∞æÏßÄ Î™ªÌïú Í≤ΩÏö∞, Î™®Îì† TDÏóêÏÑú Ïà´Ïûê Ï∞æÍ∏∞ (ÎØ∏Îûò Ï†úÏô∏)
    console.log(`No actual data found for ${country}, searching all TDs (excluding future dates)...`);
    
    for (let i = 1; i < rows.length; i++) {
      const row = rows[i];
      
      // ÎÇ†Ïßú Ï∂îÏ∂ú (YYYY-MM-DD ÌòïÏãù)
      const datePattern = /(\d{4}-\d{2}-\d{2})/;
      const dateMatch = row.match(datePattern);
      
      if (dateMatch) {
        const date = dateMatch[1];
        
        // ÎÇ†ÏßúÍ∞Ä ÌòÑÏû¨ ÎÇ†ÏßúÎ≥¥Îã§ ÎØ∏ÎûòÏù∏ÏßÄ ÌôïÏù∏
        if (date > currentDateString) {
          continue; // ÎØ∏Îûò ÎÇ†ÏßúÎäî Í±¥ÎÑàÎõ∞Í∏∞
        }
        
        // Î™®Îì† Ïà´Ïûê.Ïà´Ïûê Ìå®ÌÑ¥ Ï∞æÍ∏∞
        const numberPattern = /(\d+\.\d+)/g;
        const numberMatches = row.match(numberPattern);
        
        if (numberMatches && numberMatches.length > 0) {
          // Í∞ÄÏû• ÌÅ∞ Í∞íÏù¥ Î≥¥ÌÜµ Î©îÏù∏ Í∏àÎ¶¨
          const rates = numberMatches.map(match => parseFloat(match));
          const maxRate = Math.max(...rates);
          
          // Îçî ÏµúÏã† ÎÇ†ÏßúÏù∏ÏßÄ ÌôïÏù∏
          if (!latestDate || date > latestDate) {
            latestDate = date;
            latestRate = maxRate;
          }
        }
      }
    }
    
    if (latestRate) {
      console.log(`Found latest rate from all TDs for ${country}: ${latestRate}% (Date: ${latestDate})`);
      return latestRate;
    }
    
    // ÌÖåÏù¥Î∏îÏóêÏÑú Ï∞æÏßÄ Î™ªÌïú Í≤ΩÏö∞, Í∏∞Ï°¥ Ìå®ÌÑ¥ÏúºÎ°ú Î∞±ÏóÖ ÏãúÎèÑ
    console.log(`No rate found in table for ${country}, trying backup patterns...`);
    
    // Î∞±ÏóÖ: Ï≤´ Î≤àÏß∏ Î¨∏Ïû•ÏóêÏÑú Í∏àÎ¶¨ Ï∂îÏ∂ú
    const firstSentenceMatch = html.match(/<p[^>]*>([^<]+)<\/p>/i) || 
                              html.match(/<div[^>]*>([^<]+)<\/div>/i) ||
                              html.match(/<span[^>]*>([^<]+)<\/span>/i);
    
    const textToSearch = firstSentenceMatch ? firstSentenceMatch[1] : html;
    
    const patterns = [
      /(\d+\.\d+)%/g,
      /yield.*?(\d+\.\d+)%/i,
      /(\d+\.\d+)%.*?yield/i,
      /bond.*?(\d+\.\d+)%/i,
      /(\d+\.\d+)%.*?bond/i
    ];
    
    for (const pattern of patterns) {
      const matches = textToSearch.match(pattern);
      if (matches && matches.length > 0) {
        const rates = matches.map(match => {
          const rateMatch = match.match(/(\d+\.\d+)%/);
          return rateMatch ? parseFloat(rateMatch[1]) : 0;
        });
        
        const maxRate = Math.max(...rates);
        if (maxRate > 0) {
          console.log(`Found rate from backup pattern for ${country}: ${maxRate}%`);
          return maxRate;
        }
      }
    }
    
    console.log(`No rate found for ${country}`);
    return null;
  } catch (error) {
    console.error('Error extracting data from HTML:', error);
    return null;
  }
};

