import React from 'react';
import styled from 'styled-components';
import PageLayout from './PageLayout';

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
  margin-bottom: 30px;
`;

const MetricCard = styled.div`
  background: rgba(255, 255, 255, 0.15);
  border-radius: 15px;
  padding: 25px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const MetricTitle = styled.h3`
  color: white;
  font-size: 1.3rem;
  margin-bottom: 15px;
  font-weight: 600;
`;

const MetricValue = styled.div`
  color: #f59e0b;
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 10px;
`;

const MetricChange = styled.div`
  color: ${props => props.isPositive ? '#4ade80' : '#f87171'};
  font-size: 1rem;
  font-weight: 500;
`;

const ChartPlaceholder = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  padding: 40px;
  text-align: center;
  color: white;
  border: 2px dashed rgba(255, 255, 255, 0.3);
`;

const FXPage = () => {
  const fxMetrics = [
    { title: 'USD/KRW', value: '1,345.67', change: '+2.34', isPositive: false },
    { title: 'EUR/USD', value: '1.0856', change: '-0.0023', isPositive: false },
    { title: 'USD/JPY', value: '148.45', change: '+0.67', isPositive: true },
    { title: 'GBP/USD', value: '1.2654', change: '+0.0045', isPositive: true },
    { title: 'USD/CNY', value: '7.2345', change: '-0.0123', isPositive: true },
    { title: 'EUR/KRW', value: '1,459.23', change: '+1.89', isPositive: false }
  ];

  return (
    <PageLayout title="Foreign Exchange">
      <MetricsGrid>
        {fxMetrics.map((metric, index) => (
          <MetricCard key={index}>
            <MetricTitle>{metric.title}</MetricTitle>
            <MetricValue>{metric.value}</MetricValue>
            <MetricChange isPositive={metric.isPositive}>
              {metric.change}
            </MetricChange>
          </MetricCard>
        ))}
      </MetricsGrid>

      <ChartPlaceholder>
        <h3>외환 시장 차트</h3>
        <p>여기에 실시간 외환 차트가 표시됩니다</p>
        <p>💱 🌍 📊</p>
      </ChartPlaceholder>
    </PageLayout>
  );
};

export default FXPage;
