        console.log(`✅ ${title}: ${data.value}% (${data.isRealData ? 'Real' : 'Dummy'})`);
        return data;
      }).catch(error => {
        console.log(`❌ Error fetching ${title}:`, error.message);
        // 개별 실패 시 더미 데이터 사용
        const dummyData = DUMMY_FIXED_INCOME_DATA.find(item => item.title === title);
        if (dummyData) {
