  { title: '브라질 기준 금리', value: 12.25, change: 0.00, isPositive: true, symbol: 'BRAREFI' },
  
  // US Bond Yields - 2024년 12월 기준 최신 수익률
  { title: 'US 3M', value: 5.45, change: 0.02, isPositive: true, symbol: 'US3M' },
