              };
            }
          }
          return item;
        });
        
        console.log('âœ… Data updated with real interest rates');
        return updatedData;
      } else {
        console.log('âŒ Failed to fetch real interest rates, using dummy data');
        return dummyData;
      }
    } catch (scrapingError) {
      console.log('âŒ Error fetching real interest rates:', scrapingError);
      console.log('ğŸ“Š Falling back to dummy data');
      return dummyData;
    }
    
  } catch (error) {
    console.error('âŒ Error fetching all fixed income data:', error);
    return DUMMY_FIXED_INCOME_DATA;
  }
};

