        'Accept-Encoding': 'gzip, deflate, br',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1'
      }
    });

    console.log(`üìä Proxy ${proxyIndex + 1} response status: ${response.status}`);
    console.log(`üìä Proxy ${proxyIndex + 1} response headers:`, Object.fromEntries(response.headers.entries()));

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    
    console.log(`üìä Proxy ${proxyIndex + 1} response data type:`, typeof data);
    console.log(`üìä Proxy ${proxyIndex + 1} response data keys:`, Object.keys(data));
    
    if (!data || !data.chart || !data.chart.result || data.chart.result.length === 0) {
      console.log(`‚ùå Proxy ${proxyIndex + 1} returned invalid data structure:`, data);
      throw new Error('No valid data received');
    }
    
    console.log(`‚úÖ Proxy ${proxyIndex + 1} successful!`);
    return data;
  } catch (error) {
    console.log(`‚ùå Proxy ${proxyIndex + 1} failed: ${error.message}`);
    if (error.message.includes('JSON')) {
      console.log(`‚ùå JSON parsing error - response might be HTML error page`);
    }
    return fetchWithProxy(url, proxyIndex + 1);
  }
};

