const fetchFXDataFromYahoo = async (symbol) => {
  try {
    console.log(`üîó Fetching ${symbol} from Yahoo Finance...`);
    
    const url = `https://query1.finance.yahoo.com/v8/finance/chart/${symbol}?interval=1d&range=1d`;
    console.log(`üîó Full URL: ${url}`);
    
    const data = await fetchWithProxy(url);
    
    console.log(`üìä Data received for ${symbol}:`, {
      hasChart: !!data.chart,
      hasResult: !!data.chart?.result,
      resultLength: data.chart?.result?.length,
      hasMeta: !!data.chart?.result?.[0]?.meta
    });
    
    const result = data.chart.result[0];
    const meta = result.meta;
    
    console.log(`üìä Meta data for ${symbol}:`, {
      regularMarketPrice: meta.regularMarketPrice,
      previousClose: meta.previousClose,
      currency: meta.currency,
      symbol: meta.symbol
    });
    
    const regularMarketPrice = meta.regularMarketPrice;
    const previousClose = meta.previousClose;
    
    if (regularMarketPrice && previousClose) {
      const change = regularMarketPrice - previousClose;
      const changePercent = (change / previousClose) * 100;
      
      console.log(`‚úÖ ${symbol}: ${regularMarketPrice} (${changePercent > 0 ? '+' : ''}${changePercent.toFixed(2)}%)`);
      
      return {
        price: regularMarketPrice,
        change: change,
        changePercent: changePercent,
        previousClose: previousClose,
        isPositive: change >= 0
      };
