import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PageLayout from './PageLayout';
import StockChart from './StockChart';
import { fetchMultipleStockQuotes, fetchStockChartData, POPULAR_STOCKS } from '../services/stockApi';

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 20px;
  margin-bottom: 30px;
`;

const MetricCard = styled.div`
  background: rgba(255, 255, 255, 0.15);
  border-radius: 15px;
  padding: 25px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const MetricTitle = styled.h3`
  color: white;
  font-size: 1.3rem;
  margin-bottom: 15px;
  font-weight: 600;
`;

const MetricValue = styled.div`
  color: #4ade80;
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 10px;
`;

const MetricChange = styled.div`
  color: ${props => props.isPositive ? '#4ade80' : '#f87171'};
  font-size: 1rem;
  font-weight: 500;
  margin-bottom: 8px;
`;

const ReturnsContainer = styled.div`
  display: flex;
  gap: 15px;
  margin-top: 15px;
  padding-top: 15px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
`;

const ReturnItem = styled.div`
  flex: 1;
  text-align: center;
`;

const ReturnLabel = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.8rem;
  margin-bottom: 4px;
  font-weight: 500;
`;

const ReturnValue = styled.div`
  color: ${props => props.isPositive ? '#4ade80' : '#f87171'};
  font-size: 1.1rem;
  font-weight: 600;
`;

const MetricDetails = styled.div`
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.9rem;
  margin-top: 10px;
`;

const LoadingCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  padding: 25px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  text-align: center;
  color: white;
`;

const RefreshButton = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  border-radius: 10px;
  padding: 12px 24px;
  color: white;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-bottom: 20px;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: translateY(-2px);
  }
`;

// Ïà´Ïûê Ìè¨Îß∑ÌåÖ Ìï®Ïàò
const formatNumber = (value, decimals = 2) => {
  if (value === null || value === undefined || isNaN(value)) {
    return 'N/A';
  }
  
  // ÏÜåÏàòÏ†ê ÎëòÏß∏ÏûêÎ¶¨ÍπåÏßÄ Î∞òÏò¨Î¶º
  const roundedValue = Math.round(value * Math.pow(10, decimals)) / Math.pow(10, decimals);
  
  // Ï≤ú Îã®ÏúÑ ÏΩ§ÎßàÏôÄ ÏÜåÏàòÏ†ê ÌëúÏãú
  return roundedValue.toLocaleString('ko-KR', {
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals
  });
};

// ÌÜµÌôîÎ≥Ñ Ìè¨Îß∑ÌåÖ Ìï®Ïàò
const formatCurrency = (value, currency = 'USD', decimals = 2) => {
  if (value === null || value === undefined || isNaN(value)) {
    return 'N/A';
  }
  
  const symbol = currency === 'USD' ? '$' : currency === 'KRW' ? '‚Ç©' : '';
  const formattedValue = formatNumber(value, decimals);
  
  return `${symbol}${formattedValue}`;
};

// ÌçºÏÑºÌä∏ Ìè¨Îß∑ÌåÖ Ìï®Ïàò
const formatPercent = (value, decimals = 2) => {
  if (value === null || value === undefined || isNaN(value)) {
    return 'N/A';
  }
  
  const sign = value >= 0 ? '+' : '';
  const formattedValue = formatNumber(Math.abs(value), decimals);
  
  return `${sign}${formattedValue}%`;
};

const StockPage = () => {
  const [stockData, setStockData] = useState([]);
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [chartLoading, setChartLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedStock, setSelectedStock] = useState('^GSPC'); // S&P 500 Í∏∞Î≥∏ ÏÑ†ÌÉù
  const [timeRange, setTimeRange] = useState('1mo');

  // Ï£ºÏãù Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchStockData = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await fetchMultipleStockQuotes(POPULAR_STOCKS);
      setStockData(data);
    } catch (err) {
      setError('Ï£ºÏãù Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
      console.error('Error fetching stock data:', err);
    } finally {
      setLoading(false);
    }
  };

  // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchChartData = async (symbol, range) => {
    try {
      setChartLoading(true);
      setError(null);
      const data = await fetchStockChartData(symbol, range);
      setChartData(data);
    } catch (err) {
      setError('Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
      console.error('Error fetching chart data:', err);
    } finally {
      setChartLoading(false);
    }
  };

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    fetchStockData();
  }, []);

  // ÏÑ†ÌÉùÎêú Ï£ºÏãùÏù¥ÎÇò ÏãúÍ∞Ñ Î≤îÏúÑ Î≥ÄÍ≤Ω Ïãú Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    if (selectedStock) {
      fetchChartData(selectedStock, timeRange);
    }
  }, [selectedStock, timeRange]);

  // Ï£ºÏãù ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handleStockSelect = (symbol) => {
    setSelectedStock(symbol);
  };

  // ÏãúÍ∞Ñ Î≤îÏúÑ Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleTimeRangeChange = (range) => {
    setTimeRange(range);
  };

  // Ï£ºÏãù Ïù¥Î¶Ñ Îß§Ìïë
  const getStockDisplayName = (symbol) => {
    const names = {
      '^GSPC': 'S&P 500',
      '^IXIC': 'NASDAQ',
      '^DJI': 'DOW JONES',
      '^VIX': 'VIX',
      '^KS11': 'KOSPI',
      '^KQ11': 'KOSDAQ'
    };
    return names[symbol] || symbol;
  };

  if (loading) {
    return (
      <PageLayout title="Stock Market">
        <LoadingCard>Ï£ºÏãù Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</LoadingCard>
      </PageLayout>
    );
  }

  return (
    <PageLayout title="Stock Market">
      <RefreshButton onClick={fetchStockData}>
        üîÑ Îç∞Ïù¥ÌÑ∞ ÏÉàÎ°úÍ≥†Ïπ®
      </RefreshButton>

      <MetricsGrid>
        {stockData.map((stock, index) => (
          <MetricCard 
            key={stock.symbol} 
            onClick={() => handleStockSelect(stock.symbol)}
            style={{ 
              cursor: 'pointer',
              border: selectedStock === stock.symbol ? '2px solid #4ade80' : undefined
            }}
          >
            <MetricTitle>{getStockDisplayName(stock.symbol)}</MetricTitle>
            {stock.error ? (
              <div style={{ color: '#f87171' }}>Îç∞Ïù¥ÌÑ∞ Ïò§Î•ò</div>
            ) : (
              <>
                <MetricValue>
                  {formatCurrency(stock.price, stock.currency)}
                </MetricValue>
                <MetricChange isPositive={stock.changePercent >= 0}>
                  {formatPercent(stock.changePercent)}
                </MetricChange>
                
                <ReturnsContainer>
                  <ReturnItem>
                    <ReturnLabel>3Í∞úÏõî</ReturnLabel>
                    <ReturnValue isPositive={stock.threeMonthReturn >= 0}>
                      {formatPercent(stock.threeMonthReturn)}
                    </ReturnValue>
                  </ReturnItem>
                  <ReturnItem>
                    <ReturnLabel>YTD</ReturnLabel>
                    <ReturnValue isPositive={stock.ytdReturn >= 0}>
                      {formatPercent(stock.ytdReturn)}
                    </ReturnValue>
                  </ReturnItem>
                </ReturnsContainer>
                
                <MetricDetails>
                  Ï†ÑÏùº: {formatCurrency(stock.previousClose, stock.currency)} | 
                  Í±∞ÎûòÎüâ: {formatNumber(stock.volume, 0)}
                </MetricDetails>
              </>
            )}
          </MetricCard>
        ))}
      </MetricsGrid>

      {selectedStock && (
        <StockChart
          symbol={getStockDisplayName(selectedStock)}
          data={chartData}
          loading={chartLoading}
          error={error}
          timeRange={timeRange}
          onTimeRangeChange={handleTimeRangeChange}
        />
      )}
    </PageLayout>
  );
};

export default StockPage;
