import React from 'react';
import styled from 'styled-components';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Area, AreaChart } from 'recharts';

const ChartContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  padding: 20px;
  margin-bottom: 20px;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const ChartHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
  color: white;
`;

const ChartTitle = styled.h3`
  margin: 0;
  font-size: 1.2rem;
  font-weight: 600;
`;

const TimeRangeSelector = styled.div`
  display: flex;
  gap: 10px;
`;

const TimeButton = styled.button`
  background: ${props => props.active ? 'rgba(255, 255, 255, 0.3)' : 'rgba(255, 255, 255, 0.1)'};
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  padding: 8px 16px;
  color: white;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const LoadingMessage = styled.div`
  text-align: center;
  color: white;
  padding: 40px;
  font-size: 1.1rem;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: #f87171;
  padding: 40px;
  font-size: 1.1rem;
`;

const StockChart = ({ symbol, data, loading, error, timeRange, onTimeRangeChange }) => {
  if (loading) {
    return (
      <ChartContainer>
        <LoadingMessage>차트 데이터를 불러오는 중...</LoadingMessage>
      </ChartContainer>
    );
  }

  if (error) {
    return (
      <ChartContainer>
        <ErrorMessage>차트 데이터를 불러올 수 없습니다: {error}</ErrorMessage>
      </ChartContainer>
    );
  }

  if (!data || data.length === 0) {
    return (
      <ChartContainer>
        <ErrorMessage>표시할 데이터가 없습니다.</ErrorMessage>
      </ChartContainer>
    );
  }

  // 차트 데이터 포맷팅
  const chartData = data.map(item => ({
    ...item,
    price: parseFloat(item.close) || 0
  }));

  // 가격 범위 계산
  const prices = chartData.map(item => item.price).filter(price => price > 0);
  const minPrice = Math.min(...prices);
  const maxPrice = Math.max(...prices);
  const priceRange = maxPrice - minPrice;
  const padding = priceRange * 0.1;

  return (
    <ChartContainer>
      <ChartHeader>
        <ChartTitle>{symbol} 차트</ChartTitle>
        <TimeRangeSelector>
          <TimeButton 
            active={timeRange === '1d'} 
            onClick={() => onTimeRangeChange('1d')}
          >
            1일
          </TimeButton>
          <TimeButton 
            active={timeRange === '5d'} 
            onClick={() => onTimeRangeChange('5d')}
          >
            5일
          </TimeButton>
          <TimeButton 
            active={timeRange === '1mo'} 
            onClick={() => onTimeRangeChange('1mo')}
          >
            1개월
          </TimeButton>
          <TimeButton 
            active={timeRange === '3mo'} 
            onClick={() => onTimeRangeChange('3mo')}
          >
            3개월
          </TimeButton>
        </TimeRangeSelector>
      </ChartHeader>

      <ResponsiveContainer width="100%" height={400}>
        <AreaChart data={chartData}>
          <defs>
            <linearGradient id="colorPrice" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#4ade80" stopOpacity={0.3}/>
              <stop offset="95%" stopColor="#4ade80" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.1)" />
          <XAxis 
            dataKey="date" 
            stroke="rgba(255, 255, 255, 0.7)"
            fontSize={12}
            tick={{ fill: 'rgba(255, 255, 255, 0.7)' }}
          />
          <YAxis 
            domain={[minPrice - padding, maxPrice + padding]}
            stroke="rgba(255, 255, 255, 0.7)"
            fontSize={12}
            tick={{ fill: 'rgba(255, 255, 255, 0.7)' }}
            tickFormatter={(value) => value.toFixed(2)}
          />
          <Tooltip 
            contentStyle={{
              backgroundColor: 'rgba(0, 0, 0, 0.8)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '8px',
              color: 'white'
            }}
            labelStyle={{ color: 'white' }}
          />
          <Area
            type="monotone"
            dataKey="price"
            stroke="#4ade80"
            strokeWidth={2}
            fill="url(#colorPrice)"
            dot={false}
            activeDot={{ r: 6, fill: '#4ade80' }}
          />
        </AreaChart>
      </ResponsiveContainer>
    </ChartContainer>
  );
};

export default StockChart;
