import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { fetchAllCommoditiesData, YAHOO_FINANCE_URLS } from '../services/commoditiesApi';

// 숫자 포맷팅 함수
const formatNumber = (num) => {
  if (num === null || num === undefined) return 'N/A';
  return num.toLocaleString('en-US', {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  });
};

const formatPercent = (num) => {
  if (num === null || num === undefined) return 'N/A';
  return `${num >= 0 ? '+' : ''}${num.toFixed(2)}`;
};

// 스타일 컴포넌트
const Container = styled.div`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 30px;
`;

const Title = styled.h1`
  color: #333;
  font-size: 2.5rem;
  margin-bottom: 10px;
  font-weight: 300;
`;

const Subtitle = styled.p`
  color: #666;
  font-size: 1.1rem;
  margin: 0;
`;

const RefreshButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 25px;
  font-size: 1rem;
  cursor: pointer;
  margin-bottom: 20px;
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
  }
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
  margin-bottom: 30px;
`;

const Card = styled.div`
  background: white;
  border-radius: 15px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  cursor: pointer;
  border: 2px solid transparent;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
    border-color: #667eea;
  }
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
`;

const CardTitle = styled.h3`
  margin: 0;
  color: #333;
  font-size: 1.2rem;
  font-weight: 600;
`;

const CardSymbol = styled.span`
  color: #666;
  font-size: 0.9rem;
  font-weight: 500;
`;

const CardValue = styled.div`
  font-size: 2rem;
  font-weight: 700;
  color: #333;
  margin-bottom: 10px;
`;

const CardChange = styled.div`
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.isPositive ? '#22c55e' : '#ef4444'};
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid rgba(255,255,255,.3);
  border-radius: 50%;
  border-top-color: #fff;
  animation: spin 1s ease-in-out infinite;
  
  @keyframes spin {
    to { transform: rotate(360deg); }
  }
`;

const DataSourceIndicator = styled.div`
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 0.8rem;
  color: #666;
  margin-top: 10px;
`;

const DataSourceDot = styled.div`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background-color: ${props => props.isRealData ? '#22c55e' : '#f59e0b'};
`;

const DataSourceInfo = styled.span`
  font-size: 0.8rem;
  color: #666;
`;

const CommoditiesPage = () => {
  const [commoditiesData, setCommoditiesData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lastUpdated, setLastUpdated] = useState(null);

  const fetchCommoditiesData = async () => {
    setLoading(true);
    try {
      const data = await fetchAllCommoditiesData();
      setCommoditiesData(data);
      setLastUpdated(new Date());
    } catch (error) {
      console.error('Error fetching commodities data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCommoditiesData();
  }, []);

  const handleCardClick = (title) => {
    const url = YAHOO_FINANCE_URLS[title];
    if (url) {
      window.open(url, '_blank');
    }
  };

  const handleRefresh = () => {
    fetchCommoditiesData();
  };

  return (
    <Container>
      <Header>
        <Title>Commodities</Title>
        <Subtitle>Real-time commodity prices from Yahoo Finance</Subtitle>
      </Header>

      <RefreshButton onClick={handleRefresh} disabled={loading}>
        {loading ? (
          <>
            <LoadingSpinner />
            {' '}데이터 새로고침 중...
          </>
        ) : (
          '데이터 새로고침'
        )}
      </RefreshButton>

      {lastUpdated && (
        <div style={{ textAlign: 'center', marginBottom: '20px', color: '#666' }}>
          마지막 업데이트: {lastUpdated.toLocaleString('ko-KR')}
        </div>
      )}

      <Grid>
        {commoditiesData.map((item, index) => (
          <Card key={index} onClick={() => handleCardClick(item.title)}>
            <CardHeader>
              <CardTitle>{item.title}</CardTitle>
              <CardSymbol>{item.symbol}</CardSymbol>
            </CardHeader>
            
            <CardValue>
              ${formatNumber(item.value)}
            </CardValue>
            
            <CardChange isPositive={item.isPositive}>
              {formatPercent(item.change)} ({formatPercent(item.change)}%)
            </CardChange>
            
            <DataSourceIndicator>
              <DataSourceDot isRealData={item.isRealData} />
              <DataSourceInfo>
                {item.isRealData ? '실시간 데이터' : '더미 데이터'}
              </DataSourceInfo>
            </DataSourceIndicator>
          </Card>
        ))}
      </Grid>
    </Container>
  );
};

export default CommoditiesPage;
