// Yahoo FinanceÏóêÏÑú ÏÉÅÌíà Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Commodities ÏÑúÎπÑÏä§
const BASE_URL = 'https://query1.finance.yahoo.com/v8/finance/chart';

// Commodities Ïã¨Î≥º Îß§Ìïë (Yahoo Finance Ïã¨Î≥º)
const COMMODITIES_SYMBOLS = {
  'Gold': 'GC=F',
  'Silver': 'SI=F',
  'Brent Crude': 'BZ=F',
  'WTI': 'CL=F',
  'Copper': 'HG=F',
  'Natural Gas': 'NG=F',
  'Soybean': 'ZS=F',
  'Wheat': 'ZW=F',
  'Corn': 'ZC=F'
};

// Yahoo Finance URL Îß§Ìïë
export const YAHOO_FINANCE_URLS = {
  'Gold': 'https://finance.yahoo.com/quote/GC=F',
  'Silver': 'https://finance.yahoo.com/quote/SI=F',
  'Brent Crude': 'https://finance.yahoo.com/quote/BZ=F',
  'WTI': 'https://finance.yahoo.com/quote/CL=F',
  'Copper': 'https://finance.yahoo.com/quote/HG=F',
  'Natural Gas': 'https://finance.yahoo.com/quote/NG=F',
  'Soybean': 'https://finance.yahoo.com/quote/ZS=F',
  'Wheat': 'https://finance.yahoo.com/quote/ZW=F',
  'Corn': 'https://finance.yahoo.com/quote/ZC=F'
};

// ÌîÑÎ°ùÏãú ÏÑúÎ≤ÑÎì§ÏùÑ ÏÇ¨Ïö©Ìïú Ïõπ Ïä§ÌÅ¨ÎûòÌïë
const PROXY_SERVICES = [
  'https://api.allorigins.win/raw?url=',
  'https://corsproxy.io/?',
  'https://thingproxy.freeboard.io/fetch/',
  'https://cors.bridged.cc/',
  'https://api.codetabs.com/v1/proxy?quest=',
  'https://cors.eu.org/',
  'https://cors-anywhere.herokuapp.com/',
  '' // ÏßÅÏ†ë Ìò∏Ï∂úÎèÑ ÏãúÎèÑ
];

// ÌîÑÎ°ùÏãúÎ•º ÏÇ¨Ïö©Ìïú Ïõπ Ïä§ÌÅ¨ÎûòÌïë
const fetchWithProxy = async (url, proxyIndex = 0) => {
  if (proxyIndex >= PROXY_SERVICES.length) {
    throw new Error('All proxy attempts failed');
  }

  try {
    const proxyUrl = PROXY_SERVICES[proxyIndex] + url;
    console.log(`üîó Trying proxy ${proxyIndex + 1}/${PROXY_SERVICES.length}: ${proxyUrl.substring(0, 50)}...`);
    
    const response = await fetch(proxyUrl, {
      method: 'GET',
      headers: {
        'Accept': 'application/json,text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
        'Accept-Language': 'en-US,en;q=0.9',
        'Accept-Encoding': 'gzip, deflate, br',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1'
      },
      timeout: 10000 // 10Ï¥à ÌÉÄÏûÑÏïÑÏõÉ
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.text();
    
    if (data.length < 100) {
      throw new Error('Response too short, likely an error page');
    }
    
    console.log(`‚úÖ Proxy ${proxyIndex + 1} successful! Data length: ${data.length} characters`);
    return data;
  } catch (error) {
    console.log(`‚ùå Proxy ${proxyIndex + 1} failed: ${error.message}`);
    return fetchWithProxy(url, proxyIndex + 1);
  }
};

// 2024ÎÖÑ 12Ïõî Í∏∞Ï§Ä ÏµúÏã† ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ (Ïã§Ï†ú ÏãúÏû• ÏÉÅÌô© Î∞òÏòÅ)
const DUMMY_COMMODITIES_DATA = [
  // Precious Metals (Í∑ÄÍ∏àÏÜç)
  { title: 'Gold', value: 2075.50, change: 12.30, isPositive: true, symbol: 'GC=F' },
  { title: 'Silver', value: 24.85, change: -0.15, isPositive: false, symbol: 'SI=F' },
  
  // Energy (ÏóêÎÑàÏßÄ)
  { title: 'Brent Crude', value: 82.45, change: 1.25, isPositive: true, symbol: 'BZ=F' },
  { title: 'WTI', value: 78.90, change: 1.10, isPositive: true, symbol: 'CL=F' },
  { title: 'Natural Gas', value: 2.85, change: -0.08, isPositive: false, symbol: 'NG=F' },
  
  // Industrial Metals (ÏÇ∞ÏóÖÏö© Í∏àÏÜç)
  { title: 'Copper', value: 3.95, change: 0.05, isPositive: true, symbol: 'HG=F' },
  
  // Agriculture (ÎÜçÏÇ∞Î¨º)
  { title: 'Soybean', value: 1250.75, change: -5.25, isPositive: false, symbol: 'ZS=F' },
  { title: 'Wheat', value: 580.50, change: 8.75, isPositive: true, symbol: 'ZW=F' },
  { title: 'Corn', value: 485.25, change: -2.50, isPositive: false, symbol: 'ZC=F' }
];

// Ïã§ÏãúÍ∞Ñ Îç∞Ïù¥ÌÑ∞ ÏãúÎÆ¨Î†àÏù¥ÏÖò (ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞Ïóê ÏïΩÍ∞ÑÏùò Î≥ÄÎèô Ï∂îÍ∞Ä)
const generateRealTimeData = () => {
  return DUMMY_COMMODITIES_DATA.map(item => {
    // ÏïΩÍ∞ÑÏùò ÎûúÎç§ Î≥ÄÎèô Ï∂îÍ∞Ä (¬±2% Î≤îÏúÑ)
    const variation = (Math.random() - 0.5) * 0.04;
    const newValue = item.value * (1 + variation);
    const newChange = newValue - item.value;
    
    return {
      ...item,
      value: Math.round(newValue * 100) / 100,
      change: Math.round(newChange * 100) / 100,
      isPositive: newChange >= 0
    };
  });
};

// Í∞úÎ≥Ñ ÏÉÅÌíà Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
export const fetchCommodityQuote = async (title) => {
  try {
    console.log(`üîÑ Fetching ${title} from Yahoo Finance...`);
    
    const symbol = COMMODITIES_SYMBOLS[title];
    if (!symbol) {
      throw new Error(`Unknown symbol: ${title}`);
    }
    
    const url = `${BASE_URL}/${symbol}?interval=1d&range=1d`;
    console.log(`üîó URL: ${url}`);
    
    const data = await fetchWithProxy(url);
    
    if (!data) {
      throw new Error('Failed to fetch data');
    }
    
    // JSON ÌååÏã± ÏãúÎèÑ
    let jsonData;
    try {
      jsonData = JSON.parse(data);
    } catch (error) {
      console.log('‚ùå Failed to parse JSON, using dummy data');
      throw new Error('Invalid JSON response');
    }
    
    // Yahoo Finance API ÏùëÎãµ Íµ¨Ï°∞ÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú
    if (jsonData.chart && jsonData.chart.result && jsonData.chart.result[0]) {
      const result = jsonData.chart.result[0];
      const meta = result.meta;
      const indicators = result.indicators;
      
      if (meta && indicators && indicators.quote && indicators.quote[0]) {
        const quote = indicators.quote[0];
        const currentPrice = meta.regularMarketPrice;
        const previousClose = meta.previousClose;
        const change = currentPrice - previousClose;
        
        console.log(`‚úÖ Successfully fetched ${title}: $${currentPrice} (${change >= 0 ? '+' : ''}${change.toFixed(2)})`);
        return {
          title: title,
          value: currentPrice,
          change: Math.round(change * 100) / 100,
          isPositive: change >= 0,
          symbol: symbol,
          isRealData: true,
          dataSource: 'Yahoo Finance'
        };
      }
    }
    
    throw new Error(`No valid data found for ${title}`);
    
  } catch (error) {
    console.error(`‚ùå Error fetching ${title}:`, error.message);
    
    // Ïã§Ìå® Ïãú ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
    const dummyData = DUMMY_COMMODITIES_DATA.find(item => item.title === title);
    if (dummyData) {
      console.log(`üìä Using dummy data for ${title}`);
      return {
        ...dummyData,
        isRealData: false,
        dataSource: 'Dummy Data (Error)',
        symbol: COMMODITIES_SYMBOLS[title] || ''
      };
    }
    
    throw error;
  }
};

// Î™®Îì† Commodities Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
export const fetchAllCommoditiesData = async () => {
  try {
    console.log('üîÑ Fetching all Commodities data from Yahoo Finance...');
    
    const promises = [];
    
    // Î™®Îì† Commodities Ïã¨Î≥ºÏóê ÎåÄÌï¥ Î≥ëÎ†¨Î°ú Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
    for (const [title, symbol] of Object.entries(COMMODITIES_SYMBOLS)) {
      promises.push(
        fetchCommodityQuote(title)
          .then(data => {
            console.log(`‚úÖ ${title}: $${data.value} (${data.isRealData ? 'Real' : 'Dummy'})`);
            return data;
          })
          .catch(error => {
            console.log(`‚ùå Error fetching ${title}:`, error.message);
            // Í∞úÎ≥Ñ Ïã§Ìå® Ïãú ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
            const dummyData = DUMMY_COMMODITIES_DATA.find(item => item.title === title);
            if (dummyData) {
              console.log(`üìä Using dummy data for ${title}`);
              return {
                ...dummyData,
                isRealData: false,
                dataSource: 'Dummy Data (Error)',
                symbol: symbol
              };
            }
            return null;
          })
      );
    }
    
    // Î™®Îì† ÏöîÏ≤≠ ÏôÑÎ£å ÎåÄÍ∏∞
    const results = await Promise.all(promises);
    
    // null Í∞í ÌïÑÌÑ∞ÎßÅ
    const validData = results.filter(data => data !== null);
    
    console.log(`‚úÖ Successfully loaded ${validData.length}/${Object.keys(COMMODITIES_SYMBOLS).length} Commodities`);
    
    // Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏
    const realDataCount = validData.filter(item => item.isRealData).length;
    if (realDataCount === 0) {
      console.log('‚ö†Ô∏è No real data available, showing dummy data');
    } else {
      console.log(`‚úÖ ${realDataCount} real Commodities loaded`);
    }
    
    return validData;
    
  } catch (error) {
    console.error('‚ùå Error fetching all Commodities data:', error);
    console.log('üìä Falling back to dummy data...');
    
    // Ï†ÑÏ≤¥ Ïã§Ìå® Ïãú ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ Î∞òÌôò
    return DUMMY_COMMODITIES_DATA.map(item => ({
      ...item,
      isRealData: false,
      dataSource: 'Dummy Data (Error)',
      symbol: COMMODITIES_SYMBOLS[item.title] || ''
    }));
  }
};

// ÏÉÅÌíà Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ (Ïã§ÏãúÍ∞Ñ ÏãúÎÆ¨Î†àÏù¥ÏÖò)
export const fetchCommodities = async () => {
  try {
    // Ïã§Ï†ú API Ìò∏Ï∂ú ÎåÄÏã† Ïã§ÏãúÍ∞Ñ ÏãúÎÆ¨Î†àÏù¥ÏÖò ÏÇ¨Ïö©
    const realTimeData = generateRealTimeData();
    return realTimeData;
  } catch (error) {
    console.error('Error fetching commodities:', error);
    return DUMMY_COMMODITIES_DATA;
  }
};
