import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PageLayout from './PageLayout';
import { fetchAllFixedIncomeData, TRADING_ECONOMICS_URLS, fetchKoreaBondYield } from '../services/fixedIncomeApi';

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(175px, 1fr));
  gap: 15px;
  margin-bottom: 30px;
`;

const MetricCard = styled.div`
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  border-radius: 12px;
  padding: 15px;
  border: 1px solid #dee2e6;
  transition: transform 0.3s ease;
  cursor: pointer;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: translateY(-3px);
    background: linear-gradient(135deg, #e9ecef 0%, #dee2e6 100%);
    border-color: #1a5f3c;
    box-shadow: 0 6px 20px rgba(26, 95, 60, 0.2);
  }
`;

const MetricTitle = styled.h3`
  color: #2c3e50;
  font-size: 1rem;
  margin-bottom: 10px;
  font-weight: 600;
  text-align: center;
`;

const MetricValue = styled.div`
  color: #1a5f3c;
  font-size: 1.3rem;
  font-weight: 700;
  margin-bottom: 8px;
  text-align: center;
`;

const MetricChange = styled.div`
  color: ${props => props.isPositive ? '#1a5f3c' : '#e74c3c'};
  font-size: 0.9rem;
  font-weight: 500;
  text-align: center;
`;

const LoadingCard = styled.div`
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  border-radius: 15px;
  padding: 25px;
  border: 1px solid #dee2e6;
  text-align: center;
  color: #2c3e50;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
`;

const RefreshButton = styled.button`
  background: linear-gradient(135deg, #1a5f3c 0%, #2d7a4f 100%);
  border: none;
  border-radius: 10px;
  padding: 12px 24px;
  color: white;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-bottom: 20px;
  box-shadow: 0 4px 15px rgba(26, 95, 60, 0.3);

  &:hover {
    background: linear-gradient(135deg, #134a30 0%, #1a5f3c 100%);
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(26, 95, 60, 0.4);
  }
`;

const ClickHint = styled.div`
  text-align: center;
  color: #7f8c8d;
  font-size: 0.9rem;
  margin-top: 20px;
  font-style: italic;
`;

// 숫자 포맷팅 함수
const formatNumber = (value, decimals = 2) => {
  if (value === null || value === undefined || isNaN(value)) {
    return 'N/A';
  }
  
  const roundedValue = Math.round(value * Math.pow(10, decimals)) / Math.pow(10, decimals);
  
  return roundedValue.toLocaleString('ko-KR', {
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals
  });
};

// 퍼센트 포맷팅 함수
const formatPercent = (value, decimals = 2) => {
  if (value === null || value === undefined || isNaN(value)) {
    return 'N/A';
  }
  
  const sign = value >= 0 ? '+' : '';
  const formattedValue = formatNumber(Math.abs(value), decimals);
  
  return `${sign}${formattedValue}%`;
};

const FixedIncomePage = () => {
  const [bondMetrics, setBondMetrics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fixed Income 데이터 가져오기 (실제 웹 스크래핑 포함)
  const fetchFixedIncomeData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log('🔄 Starting Fixed Income data fetch...');
      
      // 기본 더미 데이터 가져오기
      const data = await fetchAllFixedIncomeData();
      console.log('📊 Dummy data loaded:', data.length, 'items');
      
      // 한국 국채 수익률 실제 데이터 가져오기 시도
      try {
        console.log('🌐 Attempting web scraping for Korea bond yield...');
        const koreaBondYield = await fetchKoreaBondYield();
        
        if (koreaBondYield) {
          console.log('✅ Web scraping successful! Korea bond yield:', koreaBondYield + '%');
          
          // 한국 10Y 국채 수익률 업데이트
          const updatedData = data.map(item => {
            if (item.title === 'Korea 10Y') {
              const oldValue = item.value;
              const newValue = koreaBondYield;
              const change = newValue - oldValue;
              
              console.log(`📈 Updated Korea 10Y: ${oldValue}% → ${newValue}% (Change: ${change > 0 ? '+' : ''}${change}%)`);
              
              return {
                ...item,
                value: newValue,
                change: change,
                isPositive: change >= 0,
                isRealData: true // 실제 데이터 표시
              };
            }
            return item;
          });
          
          setBondMetrics(updatedData);
          console.log('✅ Data updated with real web scraping data');
        } else {
          console.log('❌ Web scraping failed, using dummy data');
          setBondMetrics(data);
        }
      } catch (scrapingError) {
        console.log('❌ Web scraping error:', scrapingError);
        console.log('📊 Falling back to dummy data');
        setBondMetrics(data);
      }
      
    } catch (err) {
      setError('Fixed Income 데이터를 불러올 수 없습니다.');
      console.error('❌ Error fetching fixed income data:', err);
    } finally {
      setLoading(false);
    }
  };

  // 컴포넌트 마운트 시 데이터 로드
  useEffect(() => {
    fetchFixedIncomeData();
  }, []);

  // 채권 지표 클릭 핸들러 - Trading Economics 페이지 열기
  const handleBondClick = (title) => {
    const url = TRADING_ECONOMICS_URLS[title];
    if (url) {
      window.open(url, '_blank', 'noopener,noreferrer');
    }
  };

  if (loading) {
    return (
      <PageLayout title="Fixed Income">
        <LoadingCard>Fixed Income 데이터를 불러오는 중...</LoadingCard>
      </PageLayout>
    );
  }

  return (
    <PageLayout title="Fixed Income">
      <RefreshButton onClick={fetchFixedIncomeData}>
        🔄 데이터 새로고침
      </RefreshButton>

      <MetricsGrid>
        {bondMetrics.map((metric, index) => (
          <MetricCard 
            key={index} 
            onClick={() => handleBondClick(metric.title)}
          >
            <MetricTitle>{metric.title}</MetricTitle>
            {metric.error ? (
              <div style={{ color: '#e74c3c', textAlign: 'center' }}>데이터 오류</div>
            ) : (
              <>
                <MetricValue>
                  {formatPercent(metric.value)}
                </MetricValue>
                <MetricChange isPositive={metric.isPositive}>
                  {formatPercent(metric.change)}
                </MetricChange>
              </>
            )}
          </MetricCard>
        ))}
      </MetricsGrid>

      <ClickHint>
        💡 각 지표를 클릭하면 Trading Economics에서 상세 정보를 확인할 수 있습니다
      </ClickHint>
    </PageLayout>
  );
};

export default FixedIncomePage;
